import React from "react";
import TableRow from "./TableRow";
import withRouter from "../../shared/withRouter";
// import TableColumnHead from "./TableColumnHead";
import { table_columns } from "./columns";
import { useState, useEffect } from "react";
import TableSection from "./TableSection";
import { sortList } from "./helpers";
import FilterSection from "./FilterSection"
import '../issues.css'


//Table columns

const List = (props) => {
  // const [items, setItems] = useState(props.items)
  const [columns, setColumns] = useState(table_columns)
  const [filteredItems, setFilteredItems] = useState(props.items)
  const [sortedItems, setSortedItems] = useState(props.items)
  const [changedItemsIds, setChangedItemsIds] = useState([])
  const [showItems, setShowItems] = useState(props.items)

  useEffect(()=>{
    console.log(props.items)
    setShowItems(props.items)
    setSortedItems(props.items)
    setFilteredItems(props.items)
    console.log('Items')
  }, [])

  useEffect(()=>{
    setShowItems(sortedItems)
    console.log('setting showItems')
  }, [sortedItems])

  function sortItems(items) {
    const sortedItems = sortList(items)
    setSortedItems(sortedItems)
  }

  return (
    <>
    {/* <FilterSection items={items} setShowItemsIds={setShowItemsIds} changedItemsIds={changedItemsIds} columns={columns}/> */}
    <div id="obs-log" >
      <h3><strong>{Object.keys(props.project).length > 0 ? `${props.project.name} - Issues Log`:  ''}</strong></h3>
      <p><strong>Date Printed: </strong>{`${new Date()}`}</p>
      <TableSection items={props.items} showItems={showItems} columns={columns} sortedItems={sortedItems} sortItems={sortItems}/>
    </div>
    </>
  );
};

export default withRouter((List));
