import React, { useEffect, useState } from 'react';
import NavBar from '../shared/NavBar';
import withRouter from '../shared/withRouter';
import SortableTable from '../projects/SortableTable';
// import './App.css';


const IssuesPage = (props) => {
  const [issues, setIssues] = useState([]);
  // const [filtered, setFiltered] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
 
  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };

//   const filtered = issues.filter(item =>
//     item.name.toLowerCase().includes(searchTerm.toLowerCase())
//   );

  useEffect(() => {
   //  fetchData(`/api/projects/${props.params.project_id}/issues`);
    fetch(`/api/projects/${props.params.project_id}/issues`).then((r)=>r.json()).then((data)=>setIssues(data))
  }, []);

  const fetchData = async (endpoint) => {
    const response = await fetch(endpoint);
    const data = await response.json();
    setIssues(data);
    console.log(typeof(data))
  };

  return (
    <div>
      <NavBar />
    <div className='container'>
    <h2>Your issues</h2>
    <br></br>
    <div className='form-group'>
       <input
        className='form-control'
         type="text"
         placeholder="Search..."
         value={searchTerm}
         onChange={handleChange}
       />
    </div>

   <div><SortableTable data={issues} /></div>

    </div>

    </div>
  );
}

export default withRouter(IssuesPage);
