import React, { useEffect, useState } from 'react';
import NavBar from '../shared/NavBar';
import withRouter from '../shared/withRouter';
import SortableTable from '../projects/SortableTable';
import './issues.css';
import LoadingSpinner from '../shared/LoadingSpinner'


const IssuesPage = (props) => {
   const [project, setProject] = useState({})
   const [issues, setIssues] = useState([]);
   const [loading, setLoading] = useState(true)
   
   useEffect(() => {
      fetch(`/api/projects/${props.params.project_id}`).then((r)=>r.json()).then((data)=>setProject(data))
      fetch(`/api/projects/${props.params.project_id}/issues`).then((r)=>r.json()).then((data)=>setIssues(data)).then(setLoading(false))
   }, []);

   console.log(issues)

  return (
    <div>
      <NavBar />
      <div className="obs-page">
      <nav className="nav nav-pills mt-1">
        <a
          className={`nav-link`}
          type="button"
          href={`/projects/${props.params.project_id}/insp_app/insp`}
        >
          Inspections
        </a>
        <a className={`nav-link active`} target="_blank" type="button">
          Observations
        </a>
      </nav>
          <div className="text-end mb-2">
            {/* <ObsPDFDownloadLink /> */}
          </div>
          <div className="toolbar row gx-1 mb-2">
            <div className="col-3">
              {/* <ColumnCheckBoxForm columns={props.obs_columns} /> */}
            </div>
            <div className="col-9">
              {/* <FilterForm
                columns={props.obs_columns.filter((col) => col.filterable)}
              /> */}
            </div>
          </div>
          <div className="d-flex justify-content-end">
            {/* {props.create_loading ? <span>Creating Item...<span className="spinner-grow text-primary"></span></span> : <></>} */}
            <span>
            </span>
          </div>
          <div id="obs-log" >
            <h3>
              <strong>{Object.keys(project).length > 0 ? `${project.name} - Issues Log`:  ''}</strong>
            </h3>
            <p>
              <strong>Date Printed: </strong>
              {`${new Date()}`}
            </p>
            {loading ? (
              <LoadingSpinner />
            ) : (
               <table className="table table-striped">
               <thead className="table-dark">
                 <tr>
                   <th scope="col">Project</th>
                   <th scope="col">Name</th>
                   <th scope="col">Issues</th>
                   <th scope="col">Address</th>
                   <th scope="col">Start</th>
                   <th scope="col">Completion</th>
                   <th scope="col">Company</th>
                 </tr>
               </thead>
               <tbody>
               {issues.map((issue)=>
                 <tr>
                   <td>{issue["number"]}</td>
                   <td>{issue["item_date"]}</td>
                   <td>{issue["name"]}</td>
                   <td>{issue["description"]}</td>
                   {/* <td>{issue["responses"]}</td> */}
                   <td>placeholder</td>
                   <td>{issue["action_by"]}</td>
                   <td>{issue["status"]}</td>
                 </tr>
             )}
               </tbody>
             </table>
            )}
          </div>
    </div>


    </div>
  );
}

export default withRouter(IssuesPage);
