import React from "react";
import TableRow from "./TableRow";
import withRouter from "../../shared/withRouter";
// import TableColumnHead from "./TableColumnHead";
import { table_columns } from "./columns";
import { useState, useEffect } from "react";

//Table columns


const TableSection = (props) => {
  const [columns, setColumns] = useState(table_columns)
  const [showItems, setShowItems] = useState([])

  return (
    <>
      {props.items.length > 0 ? (
        <table className="obs-table table">
          <thead>
            <tr id="obs-header-row" className="obs-header-row" key="header">
              {props.columns.map((col) => (
                <TableHeaderRow columns={columns} />
              ))}
            </tr>
          </thead>
          <tbody>
            {props.obs_filtered.map((item) => {
              return (
                <TableRow columns={props.columns} key={item.id} item={item} show_photos={false}
                />
              );
            })}
          </tbody>
        </table>
      ) : (
        <div>
          <hr></hr>
          <h5>No items to show </h5>
        </div>
      )}
    </>
  );
};

export default withRouter((TableSection));


const TableHeaderRow = (props) => {
  const [if_desc, setSortDesc] = useState(true);

  const headerClick = (e) => {
    setSortDesc(!if_desc);
    // props.sort_column(props.obs_filtered, e.target.id, if_desc);
  };

  return (
    <thead>
      <tr id="obs-header-row" className="obs-header-row" key="header">
      {props.columns.map((col) => (
      <th
        scope="col"
        style={{ width: props.column ? props.column.width : "" }}
        className={props.column ? props.column.className : ""}
      >
        <div id={props.column ? props.column.name : ""} onClick={headerClick}>
          {props.column ? props.column.label : ""}
        </div>
      </th>
      ))}
      </tr>
    </thead>
  );
};

