import React, { useState, useEffect } from 'react'
import withRouter from '../shared/withRouter';
import NavBar from '../shared/NavBar';

const ActivitiesListTable = (props) => {
   const [project, setProject] = useState({})
   const [activities, setActivities] = useState([])
   const [loading, setLoading] = useState(true)
   const [searchTerm, setSearchTerm] = useState('');

   const handleChange = (event) => {
      setSearchTerm(event.target.value);
    };
  
    const filtered = activities.filter(item =>
      item.display_name.toLowerCase().includes(searchTerm.toLowerCase())
    );

   useEffect(() => {
      fetch(`/api/projects/${props.params.project_id}`).then((r)=>r.json()).then((data)=>setProject(data))
      fetch(`/api/projects/${props.params.project_id}/issues`).then(
               (r)=>r.json()).then((data)=>setActivities(data)).then(setLoading(false))
   }, []);

   return (
      <div>
      <NavBar />
    <div className='container'>
    <h3><strong>{Object.keys(props.project).length > 0 ? `${props.project.name} - Issues Log`:  ''}</strong></h3>
    <br></br>
    <h2>Your Activities</h2>
    <br></br>
    <div className='form-group'>
       <input
        className='form-control'
         type="text"
         placeholder="Search..."
         value={searchTerm}
         onChange={handleChange}
       />
    </div>
    <table className="table table-striped">
      <thead className="table-dark">
        <tr>
          <th scope="col">Date</th>
          <th scope="col">Name</th>
          <th scope="col">Summary</th>
          <th scope="col">Status</th>
          <th scope="col">Start</th>
          <th scope="col">Completion</th>
          <th scope="col">Company</th>
        </tr>
      </thead>
      <tbody>
      {filtered.map((activity)=>
        <tr>
          <td><a href={`/app/activitys/${activity.id}`}>{activity["display_name"]}</a></td>
          <td>{activity["name"]}</td>
          <td><a href={`/app/activitys/${activity.id}/issues`}>Issues</a></td>
          <td>{activity["address"]}, {activity["city"]}, {activity["state_code"]} {activity["country_code"]}</td>
          <td>{activity["start_date"]}</td>
          <td>{activity["completion_date"]}</td>
          <td>{activity["company"]["name"]}</td>
        </tr>
    )}
      </tbody>
    </table>
    </div>

    </div>
   )

   
}

export default withRouter(ActivitiesListTable)
